mapper.select:
SELECT accommodation_id, host_id, name, views FROM accommodations
host_id = #{hostId}
ORDER BY views DESC LIMIT 5

SELECT r.accommodation_id, MAX(a.name) AS name, MAX(a.host_id) AS hostId, COUNT(r.review_id) AS reviewCount FROM reviews r LEFT JOIN accommodations a ON r.accommodation_id = a.accommodation_id
a.host_id = #{hostId}
GROUP BY r.accommodation_id ORDER BY reviewCount DESC LIMIT 5

SELECT w.accommodation_id, MAX(a.host_id) AS hostId, MAX(a.name) AS name, COUNT(w.wishlist_id) AS wishlistCount FROM wishlists w LEFT JOIN accommodations a ON w.accommodation_id = a.accommodation_id
a.host_id = #{hostId}
GROUP BY w.accommodation_id ORDER BY wishlistCount DESC LIMIT 5

SELECT p.accommodation_id, MAX(a.name) AS name, MAX(a.host_id) AS hostId, SUM(p.pay_amount) AS totalSales FROM payments p LEFT JOIN accommodations a ON p.accommodation_id = a.accommodation_id WHERE p.pay_status = '완료'
AND a.host_id = #{hostId}
GROUP BY p.accommodation_id ORDER BY totalSales DESC LIMIT 5

SELECT p.accommodation_id, MAX(a.name) AS name, MAX(a.host_id) AS hostId, SUM(p.pay_amount) AS totalSales FROM payments p LEFT JOIN accommodations a ON p.accommodation_id = a.accommodation_id WHERE p.pay_status = '완료' AND p.create_day BETWEEN DATE_FORMAT(CURDATE(), '%Y-%m-01') AND LAST_DAY(CURDATE())
AND a.host_id = #{hostId}
GROUP BY p.accommodation_id ORDER BY totalSales DESC
