mapper.select:
SELECT *
FROM payments
WHERE guest_id = #{guestId}
  AND imp_uid = #{impUid}
  AND accommodation_id = #{accommodationId}
  AND payment_id = #{paymentId}
LIMIT 1

mapper.update:
UPDATE payments
SET pay_status = #{payStatus}
WHERE guest_id = #{guestId}
  AND accommodation_id = #{accommodationId}
  AND check_in = #{checkIn}
  AND check_out = #{checkOut}
